CREATE TABLE `erpat`.`payroll` ( `id` BIGINT(10) NOT NULL AUTO_INCREMENT ,  `employee_id` BIGINT(10) NOT NULL ,  `account_id` BIGINT(10) NOT NULL ,  `payment_method_id` BIGINT(10) NOT NULL ,  `expense_id` BIGINT(10) NULL ,  `amount` DECIMAL(10, 2) NOT NULL ,  `note` TEXT NOT NULL ,  `created_on` DATETIME NOT NULL ,  `created_by` BIGINT(10) NOT NULL ,  `deleted` TINYINT(1) NOT NULL DEFAULT '0' ,    PRIMARY KEY  (`id`)) ENGINE = InnoDB;