CREATE TABLE IF NOT EXISTS `erpat`.`asset_entries` ( `id` BIGINT(10) NOT NULL AUTO_INCREMENT , `title` TEXT NOT NULL , `description` TEXT NOT NULL , `category_id` BIGINT(10) NOT NULL , `location_id` BIGINT(10) NOT NULL , `vendor_id` BIGINT(10) NOT NULL , `type` ENUM('own','lease','rental','contract','service') NOT NULL , `serial_number` TEXT NOT NULL , `brand_id` TEXT NOT NULL , `model` TEXT NOT NULL , `cost` DECIMAL(10, 2) NOT NULL , `purchase_date` DATE NOT NULL , `warranty_expiry_date` DATE NOT NULL , `created_on` DATETIME NOT NULL , `created_by` BIGINT(10) NOT NULL , `deleted` TINYINT(1) NOT NULL DEFAULT '0' , PRIMARY KEY (`id`)) ENGINE = InnoDB;